    public function run(): void
    {
        DB::table('news')->insert($this->getData());
    }

    private function getData():array
    {
        $quantityNews = 30;
        $news = [];
        $quantityJpg = 1;
        foreach ($this->arrayData as $data)
        {
            $st = Status::getEnums()[array_rand(Status::getEnums())];
            $data['status'] = $st;
            $data['img'] = '/storage/img/photo/' . $this->photos[array_rand($this->photos)];
            $data['created_at'] = now();
            $news[] = $data;
            $quantityJpg++;

        }
        for($i = 0; $i <= $quantityNews; $i++)
        {
            $st = Status::getEnums()[array_rand(Status::getEnums())];
            $news[] = [
                'id_category' => rand(1, 14),
                'title' => fake()->jobTitle(),
                'author'  => $this->getAuthor(),
                'status' => $st,
                'miniDescription' => fake()->text(100),
                'description' => fake()->text(500),
                'img' =>  '/storage/img/photo/' . $this->photos[array_rand($this->photos)],
                'created_at' => now(),
            ];
            $quantityJpg++;
        }
        return $news;
    }
    private function getAuthor(): string
    {
        $x = rand(0, count($this->name)-1);
        $name = $this->name[$x];
        $y = rand(0, count($this->surname)-1);
        $surname = $this->surname[$y];
        return $surname . ' ' . $name;
    }
